mapper.select:
select username, userid, email
from ssafy_member
where userid = #{userid} and userpwd = #{userpwd}

select admin, username, userid, email, joindate
from ssafy_member
where userid = #{userid}

mapper.update:
update ssafy_member
set username = #{username}, userpwd = #{userpwd}, email = #{email}
where userid = #{userid}
